import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Navbar from '../ui/Navbar';
import Chatbot from '../list/Chatbot';
import '../styles/RecommendPage.css';
import '../styles/Chatbot.css';

const RecommendPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [recommendations, setRecommendations] = useState([]);
  const [nickname, setNickname] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [expanded, setExpanded] = useState({});
  const [chatOpen, setChatOpen] = useState(false);

  const fetchRecommendationReason = async (movieCd) => {
    try {
      const response = await fetch(`/api/ai/chatbot/llm/reason/${movieCd}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify("Ï∂îÏ≤ú Ïù¥Ïú†Î•º ÏÉùÏÑ±Ìï† ÌÖçÏä§Ìä∏ ÏûÖÎ†•Í∞í"), // ÌïÑÏöîÌïú ÏûÖÎ†•Í∞í
      });

      if (!response.ok) {
        console.error(`Ï∂îÏ≤ú Ïù¥Ïú† ÏöîÏ≤≠ Ïã§Ìå®: ${response.status}`);
        return 'Ï∂îÏ≤ú Ïù¥Ïú†Î•º Í∞ÄÏ†∏Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.';
      }

      const data = await response.json();
      return data[0]?.llm_response || 'Ï∂îÏ≤ú Ïù¥Ïú† ÏóÜÏùå';
    } catch (error) {
      console.error(`Ï∂îÏ≤ú Ïù¥Ïú† ÏöîÏ≤≠ Ï§ë Ïò§Î•ò Î∞úÏÉù: ${error}`);
      return 'Ï∂îÏ≤ú Ïù¥Ïú†Î•º Í∞ÄÏ†∏Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.';
    }
  };

  const fetchNicknameAndRecommendations = async () => {
    const accessToken = localStorage.getItem('token');

    if (!userId || !accessToken) {
      setError('Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.');
      setLoading(false);
      return;
    }

    try {
      const nicknameResponse = await fetch(`/member/user/${userId}`, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });

      if (!nicknameResponse.ok) {
        throw new Error('ÎãâÎÑ§ÏûÑ ÏöîÏ≤≠ Ïã§Ìå®');
      }

      const nicknameData = await nicknameResponse.json();
      setNickname(nicknameData.member?.nickname || '');

      const recommendationsResponse = await fetch(`/api/ai/predict/${userId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${accessToken}`,
        },
      });

      if (!recommendationsResponse.ok) {
        throw new Error('Ï∂îÏ≤ú ÏöîÏ≤≠ Ïã§Ìå®');
      }

      const recommendationsData = await recommendationsResponse.json();

      const movies = await Promise.all(
        recommendationsData.map(async (movie) => {
          try {
            const detailResponse = await fetch(
              `/api/movies/detail/${movie.movieCd}?language=ko`,
              {
                headers: {
                  Authorization: `Bearer ${accessToken}`,
                },
              }
            );

            if (!detailResponse.ok) {
              return null;
            }

            const detailData = await detailResponse.json();
            const reason = await fetchRecommendationReason(movie.movieCd);

            return {
              id: movie.movieCd,
              title: detailData.title,
              posterPath: detailData.poster_path
                ? `https://image.tmdb.org/t/p/w500${detailData.poster_path}`
                : null,
              reason,
            };
          } catch (error) {
            return null;
          }
        })
      );

      setRecommendations(movies.filter((movie) => movie !== null));
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleMovieClick = (movieId) => {
    navigate(`/api/movies/detail/${movieId}?language=ko`);
  };

  const toggleExpand = (id) => {
    setExpanded((prev) => ({
      ...prev,
      [id]: !prev[id],
    }));
  };

  useEffect(() => {
    fetchNicknameAndRecommendations();
  }, [userId]);

  if (loading) {
    return (
      <div className="recommend-page">
        <Navbar />
        <div className="recommend-content">
          <p>Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="recommend-page">
      <Navbar />
      <div className="recommend-content">
        <h2>{nickname} ÎãòÏùÑ ÏúÑÌïú Ï∂îÏ≤ú ÏòÅÌôîÎäî?</h2>
        {error ? (
          <p className="error-message">{error}</p>
        ) : (
          <div className="movie-list">
            {recommendations.map((movie) => (
              <div className="movie-item" key={movie.id}>
                <div onClick={() => handleMovieClick(movie.id)}>
                  {movie.posterPath ? (
                    <img
                      src={movie.posterPath}
                      alt={movie.title}
                      className="movie-poster"
                    />
                  ) : (
                    <div className="no-poster">Ìè¨Ïä§ÌÑ∞ ÏóÜÏùå</div>
                  )}
                  <h3 className="movie-title">{movie.title}</h3>
                </div>

                <div className="movie-reason">
                  {expanded[movie.id] ? (
                    <>
                      <p>{movie.reason}</p>
                      <button onClick={() => toggleExpand(movie.id)}>Ï†ëÍ∏∞</button>
                    </>
                  ) : (
                    <>
                      <p>
                        {movie.reason.slice(0, 100)}
                        {movie.reason.length > 100 ? '...' : ''}
                      </p>
                      {movie.reason.length > 100 && (
                        <button onClick={() => toggleExpand(movie.id)}>ÏûêÏÑ∏Ìûà Î≥¥Í∏∞</button>
                      )}
                    </>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <button
        className="chatbot-button"
        aria-label="Ï±óÎ¥á Ïó¥Í∏∞"
        onClick={() => setChatOpen(true)}
      >
        üí¨
      </button>

      {chatOpen && (
        <Chatbot isOpen={chatOpen} onClose={() => setChatOpen(false)} />
      )}
    </div>
  );
};

export default RecommendPage;
