import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Navbar.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFilm } from '@fortawesome/free-solid-svg-icons';
import { FiLogOut } from 'react-icons/fi';

function AdminNavbar() {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('memberId');
    localStorage.removeItem('token'); // JWT 토큰 삭제
    alert('로그아웃 되었습니다.');
    navigate('/'); // 로그인 페이지로 이동
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="logo">
          <FontAwesomeIcon icon={faFilm} size="2x" alt="Movie Icon" />
        </div>
        <ul className="nav-links">
          {/* 회원 관리 */}
          <li>
            <Link to="/admin/users">회원 관리</Link>
            <ul className="dropdown">
              <li><Link to="/admin/users/manage">회원 정지</Link></li>
              <li><Link to="/admin/users/movie-info">사용자별 영화 정보 조회</Link></li>
              <li><Link to="/admin/users/survey-info">사용자별 설문조사 정보 조회</Link></li>
              <li><Link to="/admin/users/member-info">사용자별 회원 정보 조회</Link></li>
              <li><Link to="/admin/users/delete-movie">사용자가 저장한 영화 삭제</Link></li>
            </ul>
          </li>
          {/* 통계 */}
          <li>
            <Link to="/admin/stats">통계</Link>
            <ul className="dropdown">
              <li><Link to="/admin/stats/gender">전체 사용자의 성별 통계</Link></li>
              <li><Link to="/admin/stats/age">전체 사용자의 나이 통계</Link></li>
              <li><Link to="/admin/stats/genre">나이별 선호 장르 통계</Link></li>
            </ul>
          </li>
        </ul>
        {/* 로그아웃 버튼 */}
        <div className="search-bar">
          <button className="logout-button" onClick={handleLogout}>
            <FiLogOut size={20} />
          </button>
        </div>
      </div>
    </nav>
  );
}

export default AdminNavbar;
