import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom'; // useParams 추가
import '../styles/Edit.css';

const genresList = [
  '드라마', '애니메이션', '액션', '어드벤쳐', '미스터리', '가족',
  '코미디', '뮤지컬', '범죄', '공연', '공포(호러)', '다큐멘터리',
  '판타지', '성인물(에로)', 'SF', '스릴러', '전쟁', '멜로/로맨스', '기타',
];

const Edit = () => {
  const { memberId } = useParams(); // URL에서 멤버 ID를 가져옴
  const [nickname, setNickname] = useState('');
  const [password, setPassword] = useState('');
  const [actorList, setActorList] = useState([]);
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [newActor, setNewActor] = useState('');
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`/member/user/${memberId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
  
        if (response.ok) {
          const data = await response.json();
          setActorList(data.preferredActors || []); // 배우 리스트를 ID와 이름 포함 저장
          setSelectedGenres(data.preferredGenres || []);
        } else {
          alert('사용자 데이터를 불러오는 데 실패했습니다.');
        }
      } catch (error) {
        console.error('데이터 로드 오류:', error);
        alert('사용자 데이터를 불러오는 중 오류가 발생했습니다.');
      } finally {
        setLoading(false);
      }
    };
  
    fetchUserData();
  }, [memberId]);
  
  // 닉네임 수정
  const handleNicknameChange = async () => {
    if (!nickname.trim()) {
      alert('닉네임을 입력하세요.');
      return;
    }
    try {
      const token = localStorage.getItem('token');
      await fetch(`/member/nickname/${memberId}`, { // memberId 추가
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ nickname }),
      });
      alert('닉네임이 성공적으로 수정되었습니다.');
    } catch (error) {
      console.error('닉네임 수정 오류:', error);
      alert('닉네임 수정에 실패했습니다.');
    }
  };
  

  // 비밀번호 수정
  const handlePasswordChange = async () => {
    if (!password.trim()) {
      alert('비밀번호를 입력하세요.');
      return;
    }
    try {
      const token = localStorage.getItem('token');
      await fetch(`/member/password/${memberId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ password }),
      });
      alert('비밀번호가 성공적으로 수정되었습니다.');
      setPassword('');
    } catch (error) {
      console.error('비밀번호 수정 오류:', error);
      alert('비밀번호 수정에 실패했습니다.');
    }
  };

  // 배우 추가 함수
const handleAddActor = async () => {
  if (!newActor.trim()) {
    alert('배우 이름을 입력하세요.');
    return;
  }

  try {
    const token = localStorage.getItem('token');
    const response = await fetch(`/survey/${memberId}/actor`, { // memberId와 actor 추가
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`, // 쿠키 대신 토큰 사용
      },
      body: JSON.stringify(newActor),
    });

    if (response.ok) {
      setActorList([...actorList, newActor]); // 로컬 상태에 추가
      setNewActor(''); // 입력값 초기화
      alert('배우가 성공적으로 추가되었습니다.');
    } else {
      alert('배우 추가에 실패했습니다. 다시 시도해주세요.');
    }
  } catch (error) {
    console.error('배우 추가 중 오류 발생:', error);
    alert('오류가 발생했습니다. 잠시 후 다시 시도해주세요.');
  }
};

const handleRemoveActor = async ({preferredActorId}) => {
  try {
    const token = localStorage.getItem('token');
    const response = await fetch(`/survey/preferred-actors/${preferredActorId}`, {
      method: 'DELETE',
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    if (response.ok) {
      setActorList(actorList.filter((actor) => actor.id !== actorId)); // 삭제된 배우를 로컬 상태에서 제거
      alert('배우가 성공적으로 삭제되었습니다.');
    } else {
      alert('배우 삭제에 실패했습니다. 다시 시도해주세요.');
    }
  } catch (error) {
    console.error('배우 삭제 중 오류 발생:', error);
    alert('오류가 발생했습니다. 잠시 후 다시 시도해주세요.');
  }
};



  // 장르 선택/해제
  const handleGenreChange = (genre) => {
    if (selectedGenres.includes(genre)) {
      setSelectedGenres(selectedGenres.filter((item) => item !== genre));
    } else {
      setSelectedGenres([...selectedGenres, genre]);
    }
  };


  if (loading) {
    return <div>로딩 중...</div>;
  }

  return (
    <div className="edit-page">
      <h2>회원 정보 수정</h2>

      {/* 닉네임 수정 */}
      <div className="form-group">
        <label>닉네임</label>
        <input
          type="text"
          value={nickname}
          onChange={(e) => setNickname(e.target.value)}
          placeholder="닉네임을 입력하세요"
        />
        <button type="button" onClick={handleNicknameChange} className="save-button">수정</button>
      </div>

      {/* 비밀번호 수정 */}
      <div className="form-group">
        <label>비밀번호</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="비밀번호를 입력하세요"
        />
        <button type="button" onClick={handlePasswordChange} className="save-button">수정</button>
      </div>

        {/* 선호 배우 관리 */}
        <div className="form-group">
          <label>좋아하는 배우</label>
          <div className="actor-input-group">
            <input
              type="text"
              value={newActor}
              onChange={(e) => setNewActor(e.target.value)}
              placeholder="배우 이름을 입력하세요"
            />
            <button type="button" onClick={handleAddActor}>추가</button>
          </div>
          <div className="actor-list">
            {actorList.map((actor, index) => (
              <div key={index} className="actor-item">
                {actor}
                <button type="button" onClick={() => handleRemoveActor(index)}>삭제</button>
              </div>
            ))}
          </div>
        </div>

        {/* 선호 장르 관리 */}
        <div className="form-group">
          <label>좋아하는 영화 장르</label>
          <div className="checkbox-group">
            {genresList.map((genre, index) => (
              <div key={index} className="checkbox-item">
                <input
                  type="checkbox"
                  id={`genre-${index}`}
                  value={genre}
                  checked={selectedGenres.includes(genre)}
                  onChange={() => handleGenreChange(genre)}
                />
                <label htmlFor={`genre-${index}`}>{genre}</label>
              </div>
            ))}
          </div>
        </div>


      {/* 돌아가기 버튼 */}
      <button
        type="button"
        className="save-button"
        onClick={() => navigate(-1)} // 이전 페이지로 이동
      >
        돌아가기
      </button>
    </div>
  );
};

export default Edit;
