import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AdminNavbar from '../ui/AdminNavbar';
import axios from 'axios';
import '../styles/admin/AdminUsers.css'; // 기존 스타일 재사용

function AdminMovie() {
  const [movies, setMovies] = useState([]); // 영화 데이터를 저장할 상태
  const navigate = useNavigate();

  useEffect(() => {
    const memberId = localStorage.getItem('memberId');
    const token = localStorage.getItem('token');

    // 관리자 권한 확인
    if (!memberId || memberId !== '14' || !token) {
      alert('접근 권한이 없습니다.');
      navigate('/');
    }

    // 회원 데이터 가져오기
    const fetchMovies = async () => {
      try {
        const response = await axios.get('http://localhost:8080/member/users', {
          headers: {
            Authorization: `Bearer ${token}`, // 토큰 인증 헤더 추가
          },
        });
        const movieData = response.data.map((user) => ({
          id: user.member.memberId,
          memberId: user.member.memberId,
          memberName: user.member.membername,
          content: user.reviews ? user.reviews.content : 'N/A',
          star: user.reviews ? user.reviews.star : 'N/A',
        }));
        setMovies(movieData); // 영화 데이터 설정
      } catch (error) {
        console.error('영화 데이터를 가져오는 중 오류 발생:', error);
        alert('영화 데이터를 가져올 수 없습니다.');
      }
    };

    fetchMovies();
  }, [navigate]);

  return (
    <div className="admin-page">
      <AdminNavbar />
      <div className="admin-content">
        <h1>영화 기록 조회</h1>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Member ID</th>
              <th>Member Name</th>
              <th>Content</th>
              <th>Star</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {movies.map((movie, index) => (
              <tr key={index}>
                <td>{movie.id}</td>
                <td>{movie.memberId}</td>
                <td>{movie.memberName}</td>
                <td>{movie.content}</td>
                <td>{movie.star}</td>
                <td>
                  {/* 삭제 버튼은 이후 구현 예정 */}
                  <button
                    className="action-button"
                    onClick={() => alert(`영화 기록 삭제 준비 중: ${movie.id}`)}
                  >
                    삭제
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminMovie;
