import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AdminNavbar from '../ui/AdminNavbar';
import axios from 'axios';
import "../styles/admin/AdminUsers.css";

function AdminUsers() {
  const [users, setUsers] = useState([]); // 사용자 정보를 저장할 상태
  const navigate = useNavigate();

  useEffect(() => {
    const memberId = localStorage.getItem('memberId');
    const token = localStorage.getItem('token');

    // 관리자 권한 확인
    if (!memberId || memberId !== '14' || !token) {
      alert('접근 권한이 없습니다.');
      navigate('/');
    }

    // 사용자 정보 가져오기
    const fetchUsers = async () => {
      try {
        const response = await axios.get('/member/users', {
          headers: {
            Authorization: `Bearer ${token}`, // 토큰 인증 헤더 추가
          },
        });
        setUsers(response.data); // JSON 데이터 설정
      } catch (error) {
        console.error('사용자 정보를 가져오는 중 오류 발생:', error);
        alert('사용자 정보를 가져올 수 없습니다.');
      }
    };

    fetchUsers();
  }, [navigate]);

  return (
    <div>
      <AdminNavbar />
      <h1>회원 관리 페이지</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Member Name</th>
            <th>Nickname</th>
            <th>Role</th>
            <th>Member Status</th>
            <th>Created At</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={index}>
              <td>{user.member.memberId}</td>
              <td>{user.member.membername}</td>
              <td>{user.member.nickname}</td>
              <td>{user.member.role}</td>
              <td>{user.member.memberStatus}</td>
              <td>{new Date(user.member.createdAt).toLocaleDateString()}</td>
              <td>
                <button onClick={() => alert(`회원 정지 기능은 여기에 추가됩니다: ${user.member.memberId}`)}>
                  정지
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminUsers;
