import React from 'react';
import RecommendationCard from './RecommendationCard';
import '../styles/MainPage.css';

const MainPage = () => {
  const recommendations = [
    {
      title: '키워드 기반 추천',
      description: '사용자가 입력한 키워드를 바탕으로 가장 적합한 도서를 추천합니다.',
      imageUrl: '/path/to/keyword-image.png', // 이미지 경로 설정
    },
    {
      title: '설문조사 기반 추천',
      description: '설문조사를 통해 사용자의 취향을 분석하여 맞춤 도서를 추천합니다.',
      imageUrl: '/path/to/survey-image.png',
    },
    {
      title: '도서 기반 추천',
      description: '사용자가 선호하는 도서를 분석해 유사한 책을 추천합니다.',
      imageUrl: '/path/to/book-image.png',
    },
  ];

  return (
    <div className="main-page">
      <header>
        <h1>READING PLANNER</h1>
        <p>당신을 위한 가장 정확한 도서 추천 플랫폼 📚</p>
      </header>
      <div className="recommendation-list">
        {recommendations.map((item, index) => (
          <RecommendationCard
            key={index}
            title={item.title}
            description={item.description}
            imageUrl={item.imageUrl}
          />
        ))}
      </div>
      <div className="signup-section">
        <button className="signup-button">회원가입 하러 가기</button>
      </div>
    </div>
  );
};

export default MainPage;
