import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/LoginPage.css';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!username || !password) {
      alert('아이디와 비밀번호를 입력해 주세요.');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post('/member/login', {
        membername: username,
        password: password,
      });

      const { memberId, statusCode, message, accessToken } = response.data;

      if (statusCode === 200 && memberId && accessToken) {
        alert(message || '로그인 성공!');

        // Local Storage에 토큰 및 사용자 정보 저장
        localStorage.setItem('memberId', memberId);
        localStorage.setItem('token', accessToken);

        // 조건에 따라 페이지 이동
        if (memberId === 14) {
          navigate('/admin/users', { replace: true }); // 관리자 페이지로 이동
        } else {
          navigate(`/main/${memberId}`, { replace: true }); // 일반 사용자 페이지로 이동
        }
      } else {
        alert(message || '로그인 실패. 다시 시도해 주세요.');
      }
    } catch (error) {
      console.error('로그인 에러:', error);
      alert(
        error.response?.data?.message || '로그인 중 문제가 발생했습니다. 다시 시도해 주세요.'
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      <form onSubmit={handleLogin} className="login-form">
        <h2>로그인</h2>
        <input
          type="text"
          placeholder="아이디"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          disabled={loading}
        />
        <input
          type="password"
          placeholder="비밀번호"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          disabled={loading}
        />
        <button type="submit" disabled={loading}>
          {loading ? '로그인 중...' : '로그인'}
        </button>
      </form>
    </div>
  );
};

export default LoginPage;
