import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Edit.css';

const Edit = () => {
  const { memberId } = useParams(); // URL에서 memberId 가져오기
  const navigate = useNavigate();
  const [preferredGenres, setPreferredGenres] = useState([]); // 선호 장르
  const [preferredActors, setPreferredActors] = useState([]); // 선호 배우
  const [newGenre, setNewGenre] = useState(''); // 새로운 장르 추가
  const [newActor, setNewActor] = useState(''); // 새로운 배우 추가

  useEffect(() => {
    const fetchSurveyData = async () => {
      const token = localStorage.getItem('token');
      try {
        const response = await axios.get(`/survey/${memberId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setPreferredGenres(response.data.preferredGenres || []);
        setPreferredActors(response.data.preferredActors || []);
      } catch (error) {
        console.error('회원 정보 불러오기 오류:', error);
        alert('데이터를 불러오는 데 실패했습니다.');
      }
    };

    fetchSurveyData();
  }, [memberId]);

  // 선호 장르 추가
  const handleAddGenre = async () => {
    if (!newGenre.trim()) {
      alert('장르를 입력하세요.');
      return;
    }
    const token = localStorage.getItem('token');
    try {
      const response = await axios.post(`/survey/${memberId}/genre`, { genre: newGenre }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('선호 장르가 성공적으로 추가되었습니다.');
      setPreferredGenres([...preferredGenres, response.data]);
      setNewGenre('');
    } catch (error) {
      console.error('선호 장르 추가 오류:', error);
      alert('선호 장르 추가에 실패했습니다.');
    }
  };

  // 선호 장르 삭제
  const handleDeleteGenre = async (genre) => {
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`/survey/preferred-genres/${genre}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('선호 장르가 성공적으로 삭제되었습니다.');
      setPreferredGenres(preferredGenres.filter((g) => g !== genre));
    } catch (error) {
      console.error('선호 장르 삭제 오류:', error);
      alert('선호 장르 삭제에 실패했습니다.');
    }
  };

  // 선호 배우 추가
  const handleAddActor = async () => {
    if (!newActor.trim()) {
      alert('배우를 입력하세요.');
      return;
    }
    const token = localStorage.getItem('token');
    try {
      const response = await axios.post(`/survey/${memberId}/actor`, { actor: newActor }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('선호 배우가 성공적으로 추가되었습니다.');
      setPreferredActors([...preferredActors, response.data]);
      setNewActor('');
    } catch (error) {
      console.error('선호 배우 추가 오류:', error);
      alert('선호 배우 추가에 실패했습니다.');
    }
  };

  // 선호 배우 삭제
  const handleDeleteActor = async (actor) => {
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`/survey/preferred-actors/${actor}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('선호 배우가 성공적으로 삭제되었습니다.');
      setPreferredActors(preferredActors.filter((a) => a !== actor));
    } catch (error) {
      console.error('선호 배우 삭제 오류:', error);
      alert('선호 배우 삭제에 실패했습니다.');
    }
  };

  return (
    <div className="edit-page">
      <h2>선호 정보 수정</h2>
      
      {/* 선호 장르 관리 */}
      <div className="genre-section">
        <h3>선호 장르 관리</h3>
        <input
          type="text"
          value={newGenre}
          onChange={(e) => setNewGenre(e.target.value)}
          placeholder="새로운 장르 입력"
        />
        <button type="button" onClick={handleAddGenre} className="save-button">추가</button>
        <ul>
          {preferredGenres.map((genre) => (
            <li key={genre}>
              {genre} <button onClick={() => handleDeleteGenre(genre)}>삭제</button>
            </li>
          ))}
        </ul>
      </div>

      {/* 선호 배우 관리 */}
      <div className="actor-section">
        <h3>선호 배우 관리</h3>
        <input
          type="text"
          value={newActor}
          onChange={(e) => setNewActor(e.target.value)}
          placeholder="새로운 배우 입력"
        />
        <button type="button" onClick={handleAddActor} className="save-button">추가</button>
        <ul>
          {preferredActors.map((actor) => (
            <li key={actor}>
              {actor} <button onClick={() => handleDeleteActor(actor)}>삭제</button>
            </li>
          ))}
        </ul>
      </div>
      
      <button type="button" onClick={() => navigate('/')} className="back-button">돌아가기</button>
    </div>
  );
};

export default Edit;
